
public class SwapTheNumbersUsingThirdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=10,temp;
		System.out.println("the value of a before swapping is " +a);     //5
		System.out.println("The value of b before swapping is" +b);          //10
		
		temp=a;  //temp=5
		a=b;       //a=10
		b=temp;        //b=5
		System.out.println("the value of a after swapping is " +a);
		System.out.println("The value of b after swapping is" +b);
		

	}

}
