package collections;

import java.util.ArrayList;
import java.util.LinkedList;
//arbitary elements means we can add elements with different datatypes

public class HowToInitializeOrbitaryElements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		LinkedList li=new LinkedList();
		li.add("jasmi");
		li.add(0);
		li.add('j');
		li.add(1.0);
		System.out.println(li);

	}

}
